mapper.select:
SELECT

FROM user_businesses
WHERE id = #{id}
AND firebase_uid = #{uid}

SELECT

FROM user_businesses
WHERE firebase_uid = #{uid}
ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM user_businesses
WHERE firebase_uid = #{uid}

mapper.delete:

DELETE FROM user_businesses 
WHERE id = #{id} 
  AND firebase_uid = #{uid}
