mapper.insert:
INSERT INTO user_businesses (
    firebase_uid, name, start_date, project_years, tax_category, acquisition_cost,
    income_mode, total_accumulated_income, monthly_income, monthly_cost,
    loan_amount, loan_interest_rate, roi, irr, group_id
) VALUES (
    #{firebaseUid}, #{name}, #{startDate}, #{projectYears}, #{taxCategory}, #{acquisitionCost},
    #{incomeMode}, #{totalAccumulatedIncome}, #{monthlyIncome}, #{monthlyCost},
    #{loanAmount}, #{loanInterestRate}, #{roi}, #{irr}, #{groupId}
)

mapper.select:
SELECT

FROM user_businesses
WHERE id = #{id}
AND firebase_uid = #{firebaseUid}

SELECT

FROM user_businesses
WHERE firebase_uid = #{firebaseUid}
ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM user_businesses
WHERE firebase_uid = #{firebaseUid}

mapper.update:
UPDATE user_businesses
name = #{name}, group_id = #{groupId}, start_date = #{startDate}, project_years = #{projectYears}, tax_category = #{taxCategory}, acquisition_cost = #{acquisitionCost}, income_mode = #{incomeMode}, total_accumulated_income = #{totalAccumulatedIncome}, monthly_income = #{monthlyIncome}, monthly_cost = #{monthlyCost}, loan_amount = #{loanAmount}, loan_interest_rate = #{loanInterestRate}, roi = #{roi}, irr = #{irr}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id} AND firebase_uid = #{firebaseUid}
