mapper.select:
SELECT 
FROM user_credit_cards
WHERE id = #{id} AND firebase_uid = #{uid}

mapper.insert:
INSERT INTO user_credit_cards (
    firebase_uid, name, deduction_account, usage_type,
    storage_location, average_monthly_expense
) VALUES (
    #{firebaseUid}, #{name}, #{deductionAccount}, #{usageType},
    #{storageLocation}, #{averageMonthlyExpense}
)
