mapper.select:
SELECT

FROM user_portfolios
WHERE id = #{id}
AND firebase_uid = #{uid}

mapper.update:
UPDATE user_portfolios
country_code = #{countryCode}, currency = #{currency}, exchange_rate = #{exchangeRate}, market_value = #{marketValue}, realized_pnl = #{realizedPnl},
WHERE id = #{id} AND firebase_uid = #{firebaseUid}

mapper.delete:

DELETE FROM user_portfolios 
WHERE id = #{id} 
  AND firebase_uid = #{uid}
