mapper.select:
SELECT

FROM user_portfolios
WHERE id = #{id}
AND firebase_uid = #{firebaseUid}

mapper.insert:
INSERT INTO user_portfolios (
    firebase_uid, country_code, currency, exchange_rate,
    market_value, realized_pnl
) VALUES (
    #{firebaseUid}, #{countryCode}, #{currency}, #{exchangeRate},
    #{marketValue}, #{realizedPnl}
)
