    assessed_value, holding_tax_rate, actual_holding_cost,
    loan_amount, interest_rate, usage_type, monthly_rent
) VALUES (
    #{firebaseUid}, #{name}, #{age}, #{size}, #{pricePerPing}, #{totalPrice},
    #{assessedValue}, #{holdingTaxRate}, #{actualHoldingCost},
    #{loanAmount}, #{interestRate}, #{usageType}, #{monthlyRent}
)

mapper.select:
SELECT

FROM user_real_estates
WHERE id = #{id}
AND firebase_uid = #{uid}

mapper.delete:

DELETE FROM user_real_estates
WHERE id = #{id} 
  AND firebase_uid = #{uid}
