mapper.select:
SELECT 
FROM user_real_estates
WHERE id = #{id} AND firebase_uid = #{uid}

mapper.insert:
INSERT INTO user_real_estates (
    firebase_uid, name, age, size, price_per_ping, total_price,
    assessed_value, holding_tax_rate, actual_holding_cost, loan_amount,
    interest_rate, usage_type, monthly_rent
) VALUES (
    #{firebaseUid}, #{name}, #{age}, #{size}, #{pricePerPing}, #{totalPrice},
    #{assessedValue}, #{holdingTaxRate}, #{actualHoldingCost}, #{loanAmount},
    #{interestRate}, #{usageType}, #{monthlyRent}
)
